mapper.insert:

insert into exam_certificate_info (certificate_id, class_num, class_name, 
  card_id, user_name, certificate_type, 
  certificate_name, certificate_num, certificate_time, 
  expiry_date, create_time, status
  )
values (#{certificateId,jdbcType=INTEGER}, #{classNum,jdbcType=VARCHAR}, #{className,jdbcType=VARCHAR}, 
  #{cardId,jdbcType=VARCHAR}, #{userName,jdbcType=VARCHAR}, #{certificateType,jdbcType=VARCHAR}, 
  #{certificateName,jdbcType=VARCHAR}, #{certificateNum,jdbcType=VARCHAR}, #{certificateTime,jdbcType=VARCHAR}, 
  #{expiryDate,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{status,jdbcType=VARCHAR}
  )

mapper.select:
select t1.certificate_id,t3.project_name,t2.class_name,t4.pnum,t4.name,t1.certificate_type,t1.certificate_name,t1.certificate_num,t1.certificate_time,t1.expiry_date,t4.tel
from exam_certificate_info t1,exam_class_info t2,exam_project_info t3,user_info t4
where t1.class_num=t2.class_num and t3.id=t2.project_id and t1.card_id=t4.pnum

  and t2.class_num like #{classNum,jdbcType=VARCHAR}


  and t2.class_name like #{className,jdbcType=VARCHAR}


  and t1.card_id like #{cardId,jdbcType=VARCHAR}


  and t1.user_name like #{userName,jdbcType=VARCHAR}


  and t1.certificate_type = #{certificateType,jdbcType=VARCHAR}


  and t1.certificate_name like #{certificateName,jdbcType=VARCHAR}


  and t1.certificate_num like #{certificateNum,jdbcType=VARCHAR}


  and t3.project_name like #{projectName,jdbcType=VARCHAR}


  and t1.certificate_type like #{certificateType,jdbcType=VARCHAR}


  and t1.certificate_time >= #{certificateTimeBegin,jdbcType=VARCHAR}


  and t1.certificate_time <= #{certificateTimeEnd,jdbcType=VARCHAR}


  and t1.expiry_date >= #{expiryDateBegin,jdbcType=VARCHAR}


  and t1.expiry_date <= #{expiryDateEnd,jdbcType=VARCHAR}

select 

from exam_certificate_info
where class_num = #{classNum} and card_id = #{cardId}
