mapper.select:
select 
t1.id,t2.name,t2.pnum,t1.birth_date,t1.tel,t1.work_name,t1.post,t1.audit_person,t1.audit_status,t1.audit_opinion
from exam_class_project t1,user_info t2,exam_project_info t3
where t1.status != '0' and t1.user_id=t2.id and t1.project_id=t3.id

  and t1.class_id = #{classId,jdbcType=INTEGER}


  and t1.project_id = #{projectId,jdbcType=INTEGER}


  and t1.user_id = #{userId,jdbcType=INTEGER}


  and t1.apply_type = #{applyType,jdbcType=VARCHAR}


  and t1.declare_type = #{declareType,jdbcType=VARCHAR}


  and t1.remark = #{remark,jdbcType=VARCHAR}


  and t1.audit_status = #{auditStatus,jdbcType=VARCHAR}


  and t1.audit_opinion = #{auditOpinion,jdbcType=VARCHAR}


  and t1.audit_time = #{auditTime,jdbcType=TIMESTAMP}


  and t1.audit_person = #{auditPerson,jdbcType=VARCHAR}


  and t1.create_time = #{createTime,jdbcType=TIMESTAMP}


  and t1.status = #{status,jdbcType=VARCHAR}


  and t1.tel = #{tel,jdbcType=VARCHAR}


  and t2.name like #{name,jdbcType=VARCHAR}


  and t2.pnum = #{pnum,jdbcType=VARCHAR}


  and t3.project_name like #{projectName,jdbcType=VARCHAR}


  and t3.project_num like #{projectNum,jdbcType=VARCHAR}


  and t3.exam_type = #{examType,jdbcType=VARCHAR}

select t.*,t5.certificate_num,t5.certificate_time,t5.expiry_date 
from (
select 
t1.id,t1.city,t1.company_level,t1.company_type,t1.work_years,t1.continue_edu,t3.project_num,t3.project_name,t3.exam_type,t2.pnum,t2.name,t1.sex,t1.birth_date,t2.work_name,t2.school,t2.major,
t2.degree,t2.tel,t1.remark,t1.audit_status,t1.audit_opinion,t1.class_id
from exam_class_project t1,user_info t2,exam_project_info t3 
where t1.status != '0' and t1.user_id=t2.id and t1.project_id=t3.id 
) t left join exam_class_info t4 on t.class_id=t4.class_id
left join exam_certificate_info t5 on t.pnum=t5.card_id and t4.class_num=t5.class_num where t.id = #{id,jdbcType=INTEGER}

mapper.update:
   update exam_class_project set 
	audit_status = #{auditStatus,jdbcType=VARCHAR},
   audit_opinion = #{auditOpinion,jdbcType=VARCHAR},
   audit_time = #{auditTime,jdbcType=TIMESTAMP},
   audit_person = #{auditPerson,jdbcType=VARCHAR}
   where id = #{id,jdbcType=INTEGER}

mapper.select:
    select t.*,t5.result_score,t6.exam_date,t6.exam_time,t6.seat_num,t6.exam_address 
    from(
    select t1.id,t1.class_id,t2.name,t2.pnum,t1.sex,t2.work_name,t1.project_id,t3.project_name,t3.exam_type,t4.class_num 
    from exam_class_project t1,user_info t2,exam_project_info t3,exam_class_info t4 
    where t1.status != '0' and t1.user_id=t2.id and t1.project_id=t3.id and t1.audit_status='审核通过' and t1.class_id=t4.class_id
    ) t 
    left join exam_result_info t5 on t.class_num=t5.class_num and t.pnum=t5.card_id and t5.status='1' 
    left join exam_ticket_info t6 on t6.project_id=t.project_id and t.class_num=t6.class_num and t.pnum=t6.card_id and t6.status='1' where 1=1 

      and t.class_id = #{classId,jdbcType=INTEGER}


	  and t.project_name like #{projectName,jdbcType=VARCHAR}


	  and t.exam_type = #{examType,jdbcType=VARCHAR}


	  and t.pnum like #{pnum,jdbcType=VARCHAR}


	  and t.name like #{userName,jdbcType=VARCHAR}


	  and t.class_num = #{classNum,jdbcType=VARCHAR}

select 

from exam_class_project
where status != '0'

  and class_id = #{classId,jdbcType=INTEGER}


  and project_id = #{projectId,jdbcType=INTEGER}


  and user_id = #{userId,jdbcType=INTEGER}


  and apply_type = #{applyType,jdbcType=VARCHAR}


  and declare_type = #{declareType,jdbcType=VARCHAR}


  and remark = #{remark,jdbcType=VARCHAR}


  and audit_status = #{auditStatus,jdbcType=VARCHAR}


  and audit_opinion = #{auditOpinion,jdbcType=VARCHAR}


  and taudit_time = #{auditTime,jdbcType=TIMESTAMP}


  and audit_person = #{auditPerson,jdbcType=VARCHAR}


  and create_time = #{createTime,jdbcType=TIMESTAMP}
