mapper.select:

select
 ,html
 from msg_info
 where 1=1 and del_date is null

      and id = #{id,jdbcType=INTEGER} 


      and type_info_id = #{typeInfoId,jdbcType=INTEGER} 


      and title = #{title,jdbcType=VARCHAR} 


     and seq = #{seq,jdbcType=INTEGER} 


      and uid = #{uid,jdbcType=INTEGER} 


     and add_date = #{addDate,jdbcType=TIMESTAMP} 


     and type_name = #{typeName,jdbcType=VARCHAR} 


      and URL = #{url,jdbcType=VARCHAR} 


      and other = #{other,jdbcType=VARCHAR} 


     and html = #{html,jdbcType=LONGVARCHAR} 


      and department = #{department,jdbcType=VARCHAR} 


      and creator = #{creator,jdbcType=VARCHAR} 



    	and add_date > #{publishDateStar} 



    	and add_date < #{publishDateEnd} 


    order by add_date desc

select count(*) from msg_info 
where type_info_id=#{typeInfoId,jdbcType=INTEGER} and seq = #{seq}  and del_date is null 

	and id!=#{id}
