mapper.select:
select 

from study_info
where 1=1 and del_date is null 

    and activity_id = #{activityId,jdbcType=INTEGER}


    and id = #{id,jdbcType=INTEGER}


    and uid = #{uid,jdbcType=INTEGER}


    and study_type_id = #{studyTypeId,jdbcType=VARCHAR}


    and school_name = #{schoolName,jdbcType=VARCHAR}


    and in_date = #{inDate,jdbcType=TIMESTAMP}


    and graduation_date = #{graduationDate,jdbcType=TIMESTAMP}


    and education = #{education,jdbcType=VARCHAR}


    and degree = #{degree,jdbcType=VARCHAR}


    and certificate = #{certificate,jdbcType=VARCHAR}


    and del_date = #{delDate,jdbcType=TIMESTAMP}

