mapper.select:
	select 

	from zc_activity_info where 1=1 and del_date is null

         and id=#{id,jdbcType=INTEGER}


       	 and name=#{name,jdbcType=VARCHAR}


        and  business_id=#{businessId,jdbcType=INTEGER}


        and sStartdate=#{sStartdate,jdbcType=TIMESTAMP}


        and sEnddate=#{sEnddate,jdbcType=TIMESTAMP}


        and pStartdate=#{pStartdate,jdbcType=TIMESTAMP}


        and #{pEnddate,jdbcType=TIMESTAMP}


        and hEnddate=#{hEnddate,jdbcType=TIMESTAMP}


        and remark=#{remark,jdbcType=VARCHAR}


        and state=#{state,jdbcType=VARCHAR}


        and delDate=#{delDate,jdbcType=TIMESTAMP}
