mapper.insert:

insert into archives_info (id, org_no, org_name, 
  s_year, aj_type, position, 
  p_uid, add_date, remark, 
  del_date, ar_no, staffname
  )
values (#{id,jdbcType=INTEGER}, #{orgNo,jdbcType=VARCHAR}, #{orgName,jdbcType=VARCHAR}, 
  #{sYear,jdbcType=VARCHAR}, #{ajType,jdbcType=VARCHAR}, #{position,jdbcType=VARCHAR}, 
  #{pUid,jdbcType=INTEGER}, #{addDate,jdbcType=TIMESTAMP}, #{remark,jdbcType=VARCHAR}, 
  #{delDate,jdbcType=TIMESTAMP}, #{arNo,jdbcType=VARCHAR}, #{staffname,jdbcType=VARCHAR}
  )

mapper.select:
SELECT UMI.DPNO, AI.ORG_NO, AI.ORG_NAME, AI.S_YEAR, AI.AJ_TYPE, AI.POSITION, AI.P_UID, STR_TO_DATE(AI.ADD_DATE,'%Y-%m-%d') ADD_DATE, AI.STAFFNAME, AI.ID FROM USER_MG_INFO UMI LEFT JOIN ARCHIVES_INFO AI ON UMI.DPNO = AI.AR_NO WHERE UMI.DEL_DATE IS NULL
AND UMI.dpno = #{dpno}
AND AI.ORG_NO = #{orgno}
AND AI.ORG_NAME = #{orgname}
AND AI.S_YEAR = #{syear}
