mapper.select:
	SELECT pi.id as id,pi.name as name,pi.pno as pno,bepi.id as eid
FROM place_info pi INNER JOIN bg_enterprise_place_info bepi ON pi.id = bepi.place_id WHERE 1=1 and pi.ac_id =#{acId,jdbcType=INTEGER}

         and bepi.activity_enterprise_id = #{activity_enterprise_id,jdbcType=INTEGER}


         and 
         (pi.pno like CONCAT('%',#{value,jdbcType=VARCHAR},'%') or pi.name like CONCAT('%',#{value,jdbcType=VARCHAR},'%'))

	SELECT pi2.id as id,pi2.name as name,pi2.pno as pno,bepi2.id as eid 
	FROM place_info pi2 LEFT JOIN bg_enterprise_place_info bepi2 ON pi2.id = bepi2.place_id 
	WHERE pi2.is_valid = '1' and pi2.ac_id =#{acId,jdbcType=INTEGER} and pi2.id not in (

SELECT pi.id as id FROM place_info pi INNER JOIN bg_enterprise_place_info bepi ON pi.id = bepi.place_id WHERE 1=1  and pi.ac_id =#{acId,jdbcType=INTEGER}

         and bepi.activity_enterprise_id = #{activity_enterprise_id,jdbcType=INTEGER}

      )

         and 
         (pi2.pno like CONCAT('%',#{value,jdbcType=VARCHAR},'%') or pi2.name like CONCAT('%',#{value,jdbcType=VARCHAR},'%'))


      GROUP BY pi2.id
