mapper.select:
select 
t.class_id,m.project_name,
t.open_time,t.class_num,t.class_name,t.class_sum,(select count(*) from exam_class_project where class_id = t.class_id and status='1') as class_count 
from exam_class_info t,exam_project_info m
where m.id = t.project_id

  and t.class_num like #{classNum,jdbcType=VARCHAR}


  and t.class_name like #{className,jdbcType=VARCHAR}


  and t.class_sum = #{classSum,jdbcType=INTEGER}


  and t.open_time = #{openTime,jdbcType=TIMESTAMP}


  and m.project_name like #{projectName,jdbcType=VARCHAR}


  and t.status = #{status,jdbcType=VARCHAR}


  and t.create_time = #{createTime,jdbcType=TIMESTAMP}
