mapper.select:
select 

from exam_project_info
where status != '0' 

	and project_num like #{projectNum,jdbcType=VARCHAR}   


	and project_name like #{projectName,jdbcType=VARCHAR}


	and host_unit like #{hostUnit,jdbcType=VARCHAR}


	and exam_type = #{examType,jdbcType=VARCHAR}


	and exam_object like #{examObject,jdbcType=VARCHAR}


	and exam_content like #{examContent,jdbcType=VARCHAR}


	and issue_unit like #{issueUnit,jdbcType=VARCHAR}


	and exam_term = #{examTerm,jdbcType=INTEGER}


	and begin_time = #{beginTime,jdbcType=DATE}


	and end_time = #{endTime,jdbcType=DATE}


	and file_url = #{fileUrl,jdbcType=VARCHAR}


	and status = #{status,jdbcType=VARCHAR}


	and create_time = #{createTime,jdbcType=DATE}
