mapper.update:

update person_major_certificate
set del_status = #{delStatus,jdbcType=CHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT m.level 'name', COUNT(1) 'num' FROM person_major_certificate m LEFT JOIN person_basic_info b ON m.pid = b.id WHERE m.del_status = "0" and b.status = "1"
AND m.create_date >= #{t_start}
AND m.create_date <= #{t_end}
GROUP BY m.level

		select count(*) 
		from person_major_certificate s LEFT JOIN person_basic_info p ON s.pid = p.id
		 where p.status = "1" AND s.del_status = "0"

              AND s.create_date  >=  #{t_start}


