SELECT a.company, a.pro_type, a.staff, a.zg_level, a.zpno, a.id, a.uid, b.`name`, b.pnum, b.sex, b.tel FROM user_expert_info a, user_info b WHERE a.uid = b.id and a.del_date is null
and b.name like CONCAT('%',#{name},'%')
and b.pnum like CONCAT('%',#{pnum},'%')
and a.company like CONCAT('%',#{company},'%')
and b.tel like CONCAT('%',#{tel},'%')
and a.uid = #{uid}
and b.name = #{name2,jdbcType=VARCHAR} and b.pnum=#{pnum2,jdbcType=VARCHAR}
and a.pro_type = #{pro_type,jdbcType=VARCHAR}
and a.id in
#{item}

SELECT company, pro_type, staff, zg_level, DATE_FORMAT(zg_time,'%Y-%m-%d') as zg_time, zpno, id, name, place_name, pnum, sex, tel, DATE_FORMAT(birth_date,'%Y-%m-%d') as birth_date, work_name, DATE_FORMAT(IFNULL(graduation_time,""),'%Y-%m-%d')as graduation_time, school, major, degree, remark, DATE_FORMAT(NOW(), '%Y') - SUBSTRING( pnum,7,4) AS age, selected, effected FROM user_expert_info_pickup WHERE del_date is null ORDER BY FIELD(effected,'是','否')
and name like CONCAT('%',#{name},'%')
